import { useContext } from 'react';
import CartContext from '../../store/CartContext';
import CartItem from './CartItem';

import Modal from './Modal';
import classes from './Cart.module.css';


const Cart = (props) => {

    const cartContext = useContext(CartContext);

    const totalAmount = `$${cartContext.totalAmount}`;
    const hasItems = cartContext.items.length > 0;

    const cartItemRemoveHandler = (id) => {};
    const cartItemAddHandler = (item) => {};

    const cartItems = (
        <ul className={classes['cart-items']}>
            {cartContext.items.map((item) => (
            <CartItem key={item.id} title={item.title} amount={item.amount} price={item.price}
            onRemove={cartItemRemoveHandler.bind(null, item.id)} onAdd={cartItemAddHandler.bind(null, item)}/>
            ))}
        </ul>
    );

    const fixedTotalAmount = `$${totalAmount.toFixed(2)}`;

    return (
    <Modal onCloseCart={props.onCloseCart}>
        <div>{cartItems}</div>
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmount}</span>
        </div>
        <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={props.onCloseCart}>Close</button>
            {hasItems && <button className={classes.button}>Order</button>}
        </div>
    </Modal>
    );
};

export default Cart;
